import Link from 'next/link';
import { useRouter } from 'next/router';
import Layout from '../../../../components/publicLayout'

const Index = props => {
  <Layout>
      {props.posts.map(post => (
          <li key={post.id}>
          <Link href="/[year]/[month]/[slug]" params={{slug: post.slug}} as={`/${post.slug}`}>
          <a>{post.title}</a>
          </Link>
          </li>
      ))}
  </Layout>
  const router = useRouter();
  let slug = router.asPath;
  // remove "/"
  slug = slug.substr(1);
  return (
    <div>
      <Link href="/"><a>Goto Index</a></Link>
      <h1>{post.title}</h1>
      <p>This is a post!</p>
    </div>
  );
}

Index.getInitialProps = async ctx => {

  let res = await fetch(`http://localhost:8080/api/v1/posts${ctx.asPath}`);
  const post = await res.json();

  if (post.error && res) {
    res.statusCode = 404;
  }

  console.log(`Show data fetched. Count: ${post.data.id}`);

  return post;
};

export default Index;
