import Link from 'next/link';
import { useRouter } from 'next/router';

const Index = props => {
    const router = useRouter();
    let slug = router.asPath;
    // remove "/"
    slug = slug.substr(1);
    return (
      <div>
        <Link href="/"><a>Goto Index</a></Link>
        <h1>{slug}</h1>
        <p>This is a post!</p>
      </div>
    );
}

Index.getInitialProps = async ctx => {

  const jsonBody = {
      maxID: "-1"
  }
  let res = await fetch(`http://localhost:3000/api/feeds/${ctx.query.slug}`);
  const reqRes = await res.json();

  console.log(`Show data fetched. Count: ${reqRes.pagination.perPage}`);

  return {
    posts: reqRes.data.map(post => post)
  };
};

export default Index;
