import Link from 'next/link';
import { useRouter } from 'next/router';

export default function About() {
    const router = useRouter();
    let slug = router.asPath;
    // remove "/"
    slug = slug.substr(1);
    return (
      <div>
        <Link href="/"><a>Goto Index</a></Link>
        <h1>{slug}</h1>
        <p>This is a post!</p>
      </div>
    );
}

Index.getInitialProps = async function() {
  const router = useRouter();
    let slug = router.asPath;
    // remove "/"
    slug = slug.substr(1);
  const jsonBody = {
      maxID: "-1"
  }
  const res = await fetch('http://localhost:8080/api/v1/posts/' + slug, {
      method: 'post',
      body: JSON.stringify(jsonBody)
  });
  const reqRes = await res.json();

  console.log(`Show data fetched. Count: ${reqRes.pagination.perPage}`);

  return {
    posts: reqRes.data.map(post => post)
  };
};
